package org.kalibro.core.concurrent;

/**
 * A report which indicates that a {@link Task} has finished. This report is generated by a task when it finishes
 * (normally or throwing an exception). The report is passed to every {@link TaskListener} object that registered to
 * receive such reports using the {@code Task.addListener} method.
 * 
 * @author Carlos Morais
 */
public class TaskReport<T> {

	private Task task;
	private long executionTime;

	private T result;
	private Throwable error;

	protected TaskReport(Task task, long start, T result) {
		this(task, start);
		this.result = result;
	}

	protected TaskReport(Task task, long start, Throwable error) {
		this(task, start);
		this.error = error;
	}

	private TaskReport(Task task, long start) {
		this.task = task;
		executionTime = System.currentTimeMillis() - start;
	}

	public Task getTask() {
		return task;
	}

	public long getExecutionTime() {
		return executionTime;
	}

	public boolean isTaskDone() {
		return error == null;
	}

	public T getResult() {
		return result;
	}

	public Throwable getError() {
		return error;
	}
}